import { React, useState, status, useContext, useEffect} from "react";
import { Card, Form, Button, Row, Col, InputGroup } from 'react-bootstrap';
import { userContext } from "../userContext";

function CreateAccount(){
    const [validated, setValidated] = useState(false);
    const [show, setShow] = useState(true);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [formChanged, setFormChanged] = useState(false);
    const { users, setActiveUser } = useContext(userContext);

    const handleSubmit = (event) => {
        const form = event.currentTarget;

        if(form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }

        users.push({name, email, password, balance:0, id:users.length});
        setActiveUser(users[users.length-1]);

        console.log(JSON.stringify(users));
        setShow(false);

        setValidated(true);
    };

    async function handleFormChange({ target:{id, value }}){
        id === 'name' ? setName(value) : id === 'email' ? setEmail(value) : setPassword(value);
    }

    useEffect(()=>  {
        if(!name && !email && !password){
            setFormChanged(false);
        } else {
            setFormChanged(true);
        }
    }, [name, email, password]
    )

    function clearForm(){
        setName('');
        setEmail('');
        setPassword('');
        setShow(true);
        setFormChanged(false);
      }

    return (
        <Card
            bgcolor="primary"
            header="Create Account"
            status={status}
        >
            <Card.Header> Create An Account </Card.Header>
            <Card.Body>
                <Form noValidate validated={validated} onSubmit={handleSubmit}>
                    <Form.Group className="mb-3" controlId="formBasicName">
                        <Form.Label> Enter Full Name </Form.Label>
                        <Row>
                            <Col>
                                <Form.Control 
                                    required
                                    type="text"
                                    placeholder="First name" 
                                />
                            </Col>
                            <Col>
                                <Form.Control 
                                    required
                                    type="text"
                                    placeholder="Last name" 
                                />
                            </Col>
                        </Row>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="email">
                        <Form.Label>Email address</Form.Label>
                        <InputGroup hasValidation>
                            <Form.Control 
                                type="email" 
                                placeholder="email@address.com"
                                required 
                            />
                            <Form.Control.Feedback type="invalid">
                                Please enter a valid email address
                            </Form.Control.Feedback>
                        </InputGroup>
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <InputGroup hasValidation>
                            <Form.Control 
                                type="password" 
                                placeholder="password"
                                required 
                            />
                        </InputGroup>
                        <Form.Text className="text-muted">
                            Strong passwords have at least 8 characters.
                        </Form.Text>
                    </Form.Group>

                    <Button variant="primary" type="submit">
                        Create Account
                    </Button> 
                    <Button as="input" type="reset" value="Reset"/>
                </Form>
            </Card.Body>
        </Card>
    )
}

export default CreateAccount;
