import React, { useState, useContext, createContext } from 'react';
import { Form, Card} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

//--------------------------------------------------------------
const DataContext = createContext({
    saveUserInfo: () => {},
    users: [],
    updateCtxBalance: () => {},
    currentUser: {},
    updateCurrUser: () => {}
  });

  export const DataContextProvider = ({ children }) => {
    const [userList, setUserList] = useState([{      
      "name": '',
      "email": '',
      "password": '',
      "balance": 0
    }]);
    const [currUser, setCurrUser] = useState("");

    const saveUserHandler = (name, email, password, balance) => {
      let newUser = {
        "name": name,
        "email": email,
        "password": password,
        "balance": balance
      };

      setUserList(prevState => [...prevState, newUser]);
      setCurrUser(newUser);
    }

    const updateCtxBalance = (num, calc) => {
      let newBal = 0;

      if (calc === "ADD") {
        setUserList(prevState => {
          for (let i = 0; i < prevState.length; i++) {
            if (prevState[i].email === currUser.email) {
              newBal = parseInt(prevState[i].balance) + parseInt(num);
              prevState[i].balance = newBal
            }
          }
          return prevState;
        })
      } else if(calc === "MINUS") {
        setUserList(prevState => {
          for (let i = 0; i < prevState.length; i++) {
            if (prevState[i].email === currUser.email) {
              newBal = parseInt(prevState[i].balance) - parseInt(num)
              prevState[i].balance = newBal
            }
          }
          return prevState;
        })
      }

      setCurrUser(prevState => {
        return {...prevState, "balance":newBal}
      })
    }

    const updateCurrUser = (username) => {
      let loggedInUser = userList.filter(user => user.email === username)[0];

      setCurrUser(loggedInUser);
    }

    let myValue = {
      saveUserInfo: saveUserHandler,
      users: userList,
      updateCtxBalance: updateCtxBalance,
      currentUser: currUser,
      updateCurrUser: updateCurrUser
    }

    return (
      <DataContext.Provider
        value={myValue}
      >
        {children}
      </DataContext.Provider>
    )
  }
//------------------------------------------------------------------
function CreateAccount({ updateUserStatus, isUser }) {
  const [Name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isValid, setIsValid] = useState(false);

  const ctx = useContext(DataContext);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (Name === "") {
      alert("Please enter your name");
      return
    } 

    if (email === "") {
      alert("Please enter a valid email address");
      return;
    }

    if (password === "") {
      alert("Password required.")
      return;
    }

    if (password.length < 8) {
      alert("Password must contain at least 8 characters.");
      setPassword("");
    } else {
      //updateUserStatus();
      ctx.saveUserInfo(Name, email, password, 0);
      alert('Success! Account created.');
    }
  }

  const NameInput = (e) => {
    setName(e.target.value);
    setIsValid(true);
  }

  const emailInput = (e) => {
    setEmail(e.target.value);
    setIsValid(true);
  }

  const passwordInput = (e) => {
    setPassword(e.target.value);
    setIsValid(true);
  }

  return (
      <Card
        bgcolor="primary"
        header="Create Account"
      >
          <Card.Header> Create An Account </Card.Header>
        <form 
          className="acc-form"
          onSubmit={handleSubmit}
        >
          {isUser && 
            <div>
              <p>If you would like to add another account, complete form again.</p> 
            </div>}
          <label className="name-label">Full Name</label>
          <div className="name-input-container">
            <input
              type="text"
              value={Name}
              onChange={NameInput}
              placeholder="Name" 
              className="name"
              autoComplete="off"
            />
          </div>

          <div className="email-input-container">
            <label className="name-label">Email address</label>
            <input
              type="email" 
              onChange={emailInput}
              placeholder="Enter email" 
              className="email-input"
              autoComplete="off"
            />
            <Form.Text className="email-text">
              Emails will be used as your username for Logins.
            </Form.Text>
          </div>

          <div className="password-input-container">
            <label className="name-label">Password</label>
            <input
              type="password" 
              value={password}
              onChange={passwordInput}
              placeholder="Password" 
              className="password-input"
              autoComplete="off"
            />
          </div>

          <button 
            className="rd-btn reg-btn"
            type="submit"
            disabled={!isValid}
          >
            {!isUser ? 'Create Account' : 'Add Another Account'}
          </button>
        </form>
      </Card>
  );
}

export default CreateAccount;
