import React, { useState } from 'react';
import { Card, Form, Button, InputGroup } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import PropTypes from 'prop-types';

async function loginUser(credentials){
    return fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(credentials)
      })
        .then(data => data.json())
}


export default function Login({ setToken }) {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

  const handleSubmit = async e => {
    e.preventDefault();
    const token = await loginUser({
      email,
      password
    });
    setToken(token);
  }

  return (
      <Card
        bgcolor="primary"
        header="Login"
      >

          <Card.Img src="./bank.jpg" alt="Banking image" />
          <Card.ImgOverlay>
            <Card.Title className="title">Welcome To BadBank</Card.Title>
              <Card.Text className="text">
                Let us help you with all your banking needs!
              </Card.Text>
            
              <Card.Header> Login </Card.Header>
                <Card.Body>
                  <Form className="acc-form" onSubmit={handleSubmit}>
                    <Form.Group className="mb-3-email">
                      <Form.Label> Email address </Form.Label>
                      <InputGroup>
                        <Form.Control 
                            type="email"
                            placeholder="email@address.com"
                            required
                            id="email"
                            onChange={e=> setEmail(e.target.value)}
                        />
                      </InputGroup>
                    </Form.Group>

                    <Form.Group className="mb-3-password">
                      <Form.Label>Password</Form.Label>
                      <InputGroup>
                        <Form.Control
                            type="password"
                            placeholder="password"
                            required
                            id="password"
                            onChange={e=> setPassword(e.target.value)}
                        />
                      </InputGroup>
                      <Form.Text className="text-muted">
                        Password must contain at least 8 characters.
                      </Form.Text>
                    </Form.Group>

                    <Button as="input" type="submit" value="Login"/>
                  </Form>    
                </Card.Body>
          </Card.ImgOverlay> 
      </Card> 
  );
};

Login.propTypes = {
    setToken: PropTypes.func.isRequired
};
