import { React, useState, status, useContext, useEffect} from "react";
import { Card, Form, Button, Row, Col, InputGroup } from 'react-bootstrap';
import { userContext } from "../userContext";

const DisabledButton = ({ text }) => {
    return ( <Button type="submit" className="btn btn-primary" disabled > {text} </Button> );
  }

  const CardForm = (props) => {
    return (
      <>
        <h1>{props.header}</h1>
        <Card
          bgcolor="primary"
          header={props.header}
          style={{ width: '18rem' }}
        >
          {props.children}
        </Card>
      </>
    );
  }

  const TextInput = ({text, type, value, onChange}) => {
    return (
      <>
        <label htmlFor={text.toLowerCase()} >{text}</label>
        <input type={type} className="form-control" id={text.toLowerCase()} placeholder={`Enter ${text.toLowerCase()}`} value={value} onChange={onChange}/>
        <br/>
      </>
    );
  }

