import { Card, Button } from 'react-bootstrap';
import React from 'react';
import "./home.css";

function Home(){
    const user = JSON.parse(window.localStorage.getItem('user'));
    console.log(user + " +++");
    const name = user.name;
    console.log(name + " ---");
    const balance = user.balance;

    function onSubmit (){
        alert("This action will permantely delete your account. Are you sure you want to continue?");
        console.log("clicked on delete account");
        <Button onClick={onSubmit}>Yes</Button>
        //sessionStorage.removeItem('token');
        //localStorage.removeItem('user');
        //window.location.reload()
        //window.location.href = "http://localhost:3000";
     }


    return (

        <Card className="bg-dark text-white" border="light" >
            <Card.Img src="./bank.jpg" alt="Banking image" />
            <Card.ImgOverlay>
                <Card.Title className="title">Welcome To BadBank</Card.Title>
                <Card.Text className="text">
                    Thank you for trusting us with your banking needs!
                </Card.Text>
                <div className="user-home">
                    <Card.Text>
                        Username: {name} 
                    </Card.Text>
                    <Card.Text>
                        Balance: ${balance}
                    </Card.Text>
                    <Card.Text>
                        <div>
                            <Button onClick={onSubmit}>Permanently close this Account.</Button>
                        </div>
                    </Card.Text>
                </div>
            </Card.ImgOverlay>
        </Card>

    );
}

export default Home;
